import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Form from '../Router/Form';

function Attention() {
  const [measureData, setMeasureData] = useState({
    height: '',
    width: '',
  });

  const handleChange = (e) => {
    setMeasureData({
      ...measureData,
      [e.target.name]: e.target.value,
    });
  };

  const navigate = useNavigate();
  const [isOpen, setIsOpen] = useState(false);
  const [tatto_src, set_src] = useState();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsOpen(false); // Kapatma işlemini burada gerçekleştiriyoruz

    //Navigate to the another page
    navigate('/form', { state: measureData }, { replace: true });
  };

  const openModal = (event) => {
    setIsOpen(true);
    // Tıklanan elementi alın
    const clickedElement = event.target;
    // Tıklanan elementin src özelliğini alın
    const src = clickedElement.src;
    set_src(src);
  };

  return (
    <div>
      <div className='image-list'>
        {/* Image elements */}
      </div>

      {isOpen && (
        <div className="modal">
          <div className="modal-content">
            {/* Modal content */}
            <form onSubmit={handleSubmit}>
              {/* Form inputs */}
              <button type="submit" className="attention_onayla">Onayla</button>
            </form>
            <button className="attention_kapat" onClick={() => setIsOpen(false)}>Kapat</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Attention;
