import React, { useState } from 'react';
import {useLocation } from 'react-router-dom';
import { useEffect } from 'react';
import {Grid, TextField,Box,Container} from '@mui/material'
//import {FormControl,InputLabel,Input,FormHelperText} from '@mui/material'
import {Stack} from '@mui/material'
import { green } from '@mui/material/colors';


function Form() {
 
 
  const location = useLocation();
  const {width,height} = location.state || {};
  
  
  useEffect(() => {
    // "measureData" verilerini kullanarak işlemler yapabilirsiniz
    

    // Sayfa geçişini değiştirin
    
  }, [location.state,],width, height);
 

  



  

  const [formData, setFormData] = useState({
    
    height:height,
    width: width,
    firstName: '',
    lastName: '',
    phoneNumber: '',
    gender: ''
  });

  const handleChange = (e) => {

      setFormData({
        ...formData,
        [e.target.name]: e.target.value 
      });

    }
   
    const handlePhoneNumberInput = (e) => {
      if (e.key === ' ' || e.keyCode === 32) {
        e.preventDefault();
      } else {
        let input = e.target.value;
        input = input.replace(/\s/g, '');
        if (input.length > 10) {
          input = input.slice(0, 10);
        }
        // Daha fazla işlem yapabilirsiniz (ör. setFormData ile form verisini güncellemek)
      }
    };
    
  const handleSubmit = async (e) => {
    console.log(formData)
    e.preventDefault();
    const phoneNumber = formData.phoneNumber;
     if (phoneNumber.length < 10 || isNaN(phoneNumber)) {
      alert("Cep numarasini doğru formatta giriniz");
    } 
  
      try {
        const response = await fetch('http://localhost:4000/api/saveUser', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(formData)
        });
        if (response.ok) {
          console.log('Veriler başariyla sunucuya gönderildi.');
          // İşlem başarili, gerekli işlemleri gerçekleştirebilirsiniz.
        } else {
          console.error('Sunucuya veri gönderirken bir hata oluştu.');
        }
      } catch (error) {
        console.error('Sunucuya istek gönderirken bir hata oluştu:', error);
      }
    
  
  };
  

  return(
   
  //  <form>
  //   <Box className='form-box' border={1} width={500} height={500} marginLeft="30%" marginTop="4%">
  //     <Grid container direction="row" justifyContent="center" alignItems="center" spacing={3} marginTop={5}>
  //       <Grid item xs={3} className="text-field-container" width={80}>
  //         <TextField
  //           label="Full Name"
  //           variant='outlined'         
  //         />
  //         <TextField
  //           label="Full Name"
  //           variant='outlined'          
  //         />
  //         <TextField
  //           label="Full Name"
  //           variant='outlined'         
  //         />
  //       </Grid >
  //     </Grid>
  //   </Box>
  // </form>
  <Box display="flex" justifyContent="center" alignItems="center" margin="0 auto" marginTop={15}>
  <Box width={500} height={500} boxShadow={3}  bgcolor="#f3f3f3"  display="flex" justifyContent="center" alignItems="center">
    <Stack spacing={4} direction="column" width="fit-content" alignItems="center">
      <Stack spacing={4}>
        <TextField label="hello" variant="outlined" />
        <TextField label="helloo" variant="outlined" />
      </Stack>
    </Stack>
  </Box>
</Box>




  )

}
export default Form;
