import React, { useState } from 'react';
import {useLocation } from 'react-router-dom';
import { useEffect } from 'react';
import {Grid,TextField,makeStyles} from '@mui/material'


function Form() {
 
 
  const location = useLocation();
  const {width,height} = location.state || {};
  
  
  useEffect(() => {
    // "measureData" verilerini kullanarak işlemler yapabilirsiniz
    

    // Sayfa geçişini değiştirin
    
  }, [location.state,],width, height);
 

  



  

  const [formData, setFormData] = useState({
    
    height:height,
    width: width,
    firstName: '',
    lastName: '',
    phoneNumber: '',
    gender: ''
  });


  const classes = useStyle();

  const useStyle = makeStyles(theme => ({

    root: {
        '& .MuiFormControl-root':{
          width: '80%',
          margin: theme.spacing(1)
        }

    }



  }))





  const handleChange = (e) => {

      setFormData({
        ...formData,
        [e.target.name]: e.target.value 
      });

    }
   
    const handlePhoneNumberInput = (e) => {
      if (e.key === ' ' || e.keyCode === 32) {
        e.preventDefault();
      } else {
        let input = e.target.value;
        input = input.replace(/\s/g, '');
        if (input.length > 10) {
          input = input.slice(0, 10);
        }
        // Daha fazla işlem yapabilirsiniz (ör. setFormData ile form verisini güncellemek)
      }
    };
    
  const handleSubmit = async (e) => {
    console.log(formData)
    e.preventDefault();
    const phoneNumber = formData.phoneNumber;
     if (phoneNumber.length < 10 || isNaN(phoneNumber)) {
      alert("Cep numarasini doğru formatta giriniz");
    } 
  
      try {
        const response = await fetch('http://localhost:4000/api/saveUser', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(formData)
        });
        if (response.ok) {
          console.log('Veriler başariyla sunucuya gönderildi.');
          // İşlem başarili, gerekli işlemleri gerçekleştirebilirsiniz.
        } else {
          console.error('Sunucuya veri gönderirken bir hata oluştu.');
        }
      } catch (error) {
        console.error('Sunucuya istek gönderirken bir hata oluştu:', error);
      }
    
  
  };

  return(

 <form>
  <Grid container>
    
    <Grid item xs = {6}>
      <TextField
      variant="outlined"
      label = "Full Name"
      value={formData.height}
      />
      <TextField
        variant = "outlined"
        label = "Email"
        value= {formData.width}
        />
    </Grid>

  </Grid>


 </form>

)
  

}



  export default Form;
