import React, { useState } from 'react';
import { useParams,useLocation } from 'react-router-dom';
import { useEffect } from 'react';

function Form() {
 
  const location = useLocation();
  const {width,height} = location.state || {};
  
  
  useEffect(() => {
    // "measureData" verilerini kullanarak işlemler yapabilirsiniz
    window.scrollTo(0, 0);
  }, [location.state]); // Burada location.state'ı bağımlılık dizisine ekleyin


 





  

  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phoneNumber: '',
    gender: ''
  });

  const handleChange = (e) => {

      setFormData({
        ...formData,
        [e.target.name]: e.target.value 
      });

    }
   
    const handlePhoneNumberInput = (e) => {
      if (e.key === ' ' || e.keyCode === 32) {
        e.preventDefault();
      } else {
        let input = e.target.value;
        input = input.replace(/\s/g, '');
        if (input.length > 10) {
          input = input.slice(0, 10);
        }
        // Daha fazla işlem yapabilirsiniz (ör. setFormData ile form verisini güncellemek)
      }
    };
    
  const handleSubmit = async (e) => {
    e.preventDefault();
    const phoneNumber = formData.phoneNumber;
     if (phoneNumber.length < 10 || isNaN(phoneNumber)) {
      alert("Cep numarasini doğru formatta giriniz");
    } 
  
      try {
        const response = await fetch('http://localhost:4000/api/saveUser', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(formData)
        });
        if (response.ok) {
          console.log('Veriler başariyla sunucuya gönderildi.');
          // İşlem başarili, gerekli işlemleri gerçekleştirebilirsiniz.
        } else {
          console.error('Sunucuya veri gönderirken bir hata oluştu.');
        }
      } catch (error) {
        console.error('Sunucuya istek gönderirken bir hata oluştu:', error);
      }
    
  
  };

  return (
    <form onSubmit={handleSubmit} className="form-container">
      <div className="form-group">
        <label>
          İsim:
          <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} required />
        </label>
      </div>
      <div className="form-group">
        <label>
          Soyisim:
          <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} required placeholder={width}/>
        </label>
      </div>
      <div className="form-group">
        <label>
          Telefon Numarasi:
          <input type="tel" name="phoneNumber" value={formData.phoneNumber} onChange={handleChange} placeholder='535 ___ __ __' onKeyDown={handlePhoneNumberInput} maxLength={10} required   />
        </label>
      </div>
      <div className="form-group">
        <label>
          Cinsiyet:
          <select name="gender" value={formData.gender} onChange={handleChange} required>
            <option value="">Seçiniz</option>
            <option value="erkek">Erkek</option>
            <option value="kadin">Kadin</option>
          </select>
        </label>
      </div>
      <button type="submit">Kaydet</button>
    </form>
  );
  }
  
export default Form;
