import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

function Attention() {
  const history = useHistory();

  const [isOpen, setIsOpen] = useState(false);
  const [tatto_src, set_src] = useState();

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Formun submit işlemleri burada gerçekleştirilir.
    // Eğer formu başarılı bir şekilde submit ettikten sonra yönlendirmek istiyorsanız, aşağıdaki satırı kullanabilirsiniz:
    // history.push('/onaylandi');
  };

  const openModal = (event) => {
    setIsOpen(true);
    // Tıklanan elementi alın
    const clickedElement = event.target;
    // Tıklanan elementin src özelliğini alın
    const src = clickedElement.src;
    set_src(src);
  };

  const closeModal = () => {
    setIsOpen(false);
  };

  return (
    <div>
      <div className='image-list'>
        {/* Resimler */}
      </div>

      {isOpen ? (
        <div className="modal">
          <div className="modal-content">
            <img src={tatto_src} alt="hello" />
            <h1>BOYUT YAZ</h1>
            <h3 className='attention_genislik'>Genişlik</h3>
            <h3 className='attention_uzunluk'>Uzunluk</h3>
            <form onSubmit={handleSubmit}>
              <input className= "attention_genislik_input" type="text" id="text1" name="text1" placeholder="genislik cm" />
              <input className= "attention_uzunluk_input"  type="text" id="text2" name="text2" placeholder="uzunluk cm" />
              <button type="submit" className="attention_onayla" >Onayla</button>
            </form>
            <button className="attention_kapat" onClick={closeModal}>Kapat</button>
          </div>
        </div>
      ) : null}
    </div>
  );
}

export default Attention;
